import React, { useState } from 'react'
import { useForm } from 'react-hook-form'
import { Link } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'
import axios from 'axios';

function Signup() {



const styles = {
  inputBox: {
    fontFamily: 'Roboto, sans-serif',
    fontWeight: '500',
    textTransform: 'uppercase',
    color: '#2E8612',
    overflow: 'hidden',
    background: '#BADA55',
    height: '100%',
  },
  h1: {
    fontWeight: '100',
    textAlign: 'center',
    margin: '0',
    padding: '0',
    fontSize: '50px',
  },
  h4: {
    fontWeight: '400',
    textAlign: 'center',
    margin: '0',
    padding: '0',
    marginBottom: '50px',
  },
  continput: {
    position: 'absolute',
    top: '0',
    left: '0',
    right: '0',
    bottom: '0',
    margin: 'auto',
    width: '500px',
    height: '270px',
    padding: '16px',
    boxSizing: 'border-box',
  },
  ul: {
    listStyleType: 'none',
    width: '220px',
    margin: 'auto',
  },
  li: {
    position: 'relative',
    padding: '10px',
    paddingLeft: '40px',
    height: '30px',
  },
  label: {
    position: 'absolute',
    width: '15px',
    height: '15px',
    background: '#fff',
    content: '""',
    left: '7px',
    top: 'calc(50% - 13px)',
    boxSizing: 'border-box',
    borderRadius: '50%',
  },
  inputRadio: {
    opacity: '0',
    WebkitAppearance: 'none',
    display: 'inline-block',
    verticalAlign: 'middle',
    zIndex: '100',
    margin: '0',
    padding: '0',
    width: '100%',
    height: '30px',
    position: 'absolute',
    left: '0',
    top: 'calc(50% - 15px)',
    cursor: 'pointer',
  },
  bullet: {
    position: 'relative',
    width: '25px',
    height: '25px',
    left: '-3px',
    top: '2px',
    border: '5px solid #fff',
    opacity: '0',
    borderRadius: '50%',
  },
  checkedBullet: {
    position: 'absolute',
    opacity: '1',
    animationName: 'explode',
    animationDuration: '0.350s',
  },
  line: {
    position: 'absolute',
    width: '10px',
    height: '2px',
    backgroundColor: '#fff',
    opacity: '0',
  },
  lineZero: {
    left: '11px',
    top: '-21px',
    transform: 'translateY(20px)',
    width: '2px',
    height: '10px',
  },
  lineOne: {
    right: '-7px',
    top: '-11px',
    transform: 'rotate(-55deg) translate(-9px)',
  },
  lineTwo: {
    right: '-20px',
    top: '11px',
    transform: 'translate(-9px)',
  },
  lineThree: {
    right: '-8px',
    top: '35px',
    transform: 'rotate(55deg) translate(-9px)',
  },
  lineFour: {
    left: '-8px',
    top: '-11px',
    transform: 'rotate(55deg) translate(9px)',
  },
  lineFive: {
    left: '-20px',
    top: '11px',
    transform: 'translate(9px)',
  },
  lineSix: {
    left: '-8px',
    top: '35px',
    transform: 'rotate(-55deg) translate(9px)',
  },
  lineSeven: {
    left: '11px',
    bottom: '-21px',
    transform: 'translateY(-20px)',
    width: '2px',
    height: '10px',
  },
  checkedLineZero: {
    animationName: 'drop-zero',
    animationDelay: '0.100s',
    animationDuration: '0.9s',
    animationFillMode: 'forwards',
  },
  checkedLineOne: {
    animationName: 'drop-one',
    animationDelay: '0.100s',
    animationDuration: '0.9s',
    animationFillMode: 'forwards',
  },
  checkedLineTwo: {
    animationName: 'drop-two',
    animationDelay: '0.100s',
    animationDuration: '0.9s',
    animationFillMode: 'forwards',
  },
  checkedLineThree: {
    animationName: 'drop-three',
    animationDelay: '0.100s',
    animationDuration: '0.9s',
    animationFillMode: 'forwards',
  },
  checkedLineFour: {
    animationName: 'drop-four',
    animationDelay: '0.100s',
    animationDuration: '0.9s',
    animationFillMode: 'forwards',
  },
  checkedLineFive: {
    animationName: 'drop-five',
    animationDelay: '0.100s',
    animationDuration: '0.9s',
    animationFillMode: 'forwards',
  },
  checkedLineSix: {
    animationName: 'drop-six',
    animationDelay: '0.100s',
    animationDuration: '0.9s',
    animationFillMode: 'forwards',
  },
  checkedLineSeven: {
    animationName: 'drop-seven',
    animationDelay: '0.100s',
    animationDuration: '0.9s',
    animationFillMode: 'forwards',
  },
  '@keyframes explode': {
    '0%': {
      opacity: '0',
      transform: 'scale(10)',
    },
    '60%': {
      opacity: '1',
      transform: 'scale(0.5)',
    },
    '100%': {
      opacity: '1',
      transform: 'scale(1)',
    },
  },
  '@keyframes drop-zero': {
    '0%': {
      opacity: '0',
      transform: 'translateY(20px)',
      height: '10px',
    },
    '20%': {
      opacity: '1',
    },
    '100%': {
      transform: 'translateY(-2px)',
      height: '0px',
      opacity: '0',
    },
  },
  '@keyframes drop-one': {
    '0%': {
      opacity: '0',
      transform: 'rotate(-55deg) translate(-20px)',
      width: '10px',
    },
    '20%': {
      opacity: '1',
    },
    '100%': {
      transform: 'rotate(-55deg) translate(9px)',
      width: '0px',
      opacity: '0',
    },
  },
  '@keyframes drop-two': {
    '0%': {
      opacity: '0',
      transform: 'translate(-20px)',
      width: '10px',
    },
    '20%': {
      opacity: '1',
    },
    '100%': {
      transform: 'translate(9px)',
      width: '0px',
      opacity: '0',
    },
  },
  '@keyframes drop-three': {
    '0%': {
      opacity: '0',
      transform: 'rotate(55deg) translate(-20px)',
      width: '10px',
    },
    '20%': {
      opacity: '1',
    },
    '100%': {
      transform: 'rotate(55deg) translate(9px)',
      width: '0px',
      opacity: '0',
    },
  },
  '@keyframes drop-four': {
    '0%': {
      opacity: '0',
      transform: 'rotate(55deg) translate(20px)',
      width: '10px',
    },
    '20%': {
      opacity: '1',
    },
    '100%': {
      transform: 'rotate(55deg) translate(-9px)',
      width: '0px',
      opacity: '0',
    },
  },
  '@keyframes drop-five': {
    '0%': {
      opacity: '0',
      transform: 'translate(20px)',
      width: '10px',
    },
    '20%': {
      opacity: '1',
    },
    '100%': {
      transform: 'translate(-9px)',
      width: '0px',
      opacity: '0',
    },
  },
  '@keyframes drop-six': {
    '0%': {
      opacity: '0',
      transform: 'rotate(-55deg) translate(20px)',
      width: '10px',
    },
    '20%': {
      opacity: '1',
    },
    '100%': {
      transform: 'rotate(-55deg) translate(-9px)',
      width: '0px',
      opacity: '0',
    },
  },
  '@keyframes drop-seven': {
    '0%': {
      opacity: '0',
      transform: 'translateY(-20px)',
      height: '10px',
    },
    '20%': {
      opacity: '1',
    },
    '100%': {
      transform: 'translateY(2px)',
      height: '0px',
      opacity: '0',
    },
        input: {
          width: '100%',
          height: '100%',
          background: 'transparent',
          border: 'none',
          outline: 'none',
          borderRadius: '40px',
          fontSize: '16px',
          color: '#fff',
          padding: '10px 45px 10px 20px',
          border: '2px solid rgba(255, 255, 255, 0.2)',
        },
        placeholder: {  
          color: '#fff',
        },
        icon: {
          position: 'absolute',
          right: '20px',
          top: '50%',
          transform: 'translateY(-50%)',
          fontSize: '20px',
        }
      }};

    let {register,handleSubmit,formState:{errors}}=useForm()
    let [err,setErr]=useState('')
    let navigate=useNavigate()
    
   async function submit(obj){
        if(obj.usertype=="User"){
            //make http post req
            let res=await axios.post('http://localhost:4000/user-api/new-user',obj)
            if(res.data.message === 'User is created')
            {
                //navigate to the signin page
                navigate('/signin')
            }else{
                //handle the error
                setErr(res.data.message)
            }
        }else if(obj.usertype=="Author"){
        //make the http post req to author
        let res=await axios.post('http://localhost:4000/author-api/new-user',obj)
        if(res.data.message== "Author is created"){
            //navigate to signin
            navigate('/signin')
        }else{
            //Handle the error
            setErr(res.data.message)
        }
    }
    }

  return (
    <div style={{height:"100%"}} className='m-5 p-5' >
        <div className="row mx-auto card shadow-lg m-5 border-0 rounded-lg" style={{ width:"450px",height:"450",background:'transparent',backdropFilter: 'blur(20px)',borderTopLeftRadius:'50px',borderTopRightRadius:'50px',borderBottomLeftRadius:'50px',borderBottomRightRadius:'50px'}}>
            <div className="col p-4" >    
            <h1 className='text-center m-3 '>Signup</h1>
            {err.length!=0 && <p className='text-danger text-center m-2'>{err}</p>}
            <form className=' w-100  text-dark p-3 text-center' onSubmit={handleSubmit(submit)}>
            <div className="mb-3">
            <div className="form-check form-check-inline m-0 p-0">
                                <div>
                                <input className="form-check-input mx-2" type="radio" name="usertype" value={"User"} {...register('usertype',{required:true})}/>
                                <label className="form-check-label">User</label>
                                <div class="bullet">
                                    <div class="line zero"></div>
                                    <div class="line one"></div>
                                    <div class="line two"></div>
                                    <div class="line three"></div>
                                    <div class="line four"></div>
                                    <div class="line five"></div>
                                    <div class="line six"></div>
                                    <div class="line seven"></div>
                                </div>
                                </div>

                                <div>
                                <input className="form-check-input mx-2" type="radio" name="usertype" value={"Author"}{...register('usertype',{required:true})}/>
                                <label className="form-check-label">Author</label>
                                <div class="bullet">
                                    <div class="line zero"></div>
                                    <div class="line one"></div>
                                    <div class="line two"></div>
                                    <div class="line three"></div>
                                    <div class="line four"></div>
                                    <div class="line five"></div>
                                    <div class="line six"></div>
                                    <div class="line seven"></div>
                                </div>
                                </div>
                                  
                                {errors.usertype?.type==='required' && <p className='text-danger m-2'>Choose anyone</p>}
                            </div>
            </div>

            <div className='mb-3'>
                
                <input type="text" style={styles.input} className='form-control' placeholder='Username' {...register('username',{required:true})}/>
                {errors.username?.type==='required' && <p className='text-danger m-2'>Username Required</p>}

            </div>
            <div className='mb-3'>

                <input type="email" style={styles.input} className='form-control' placeholder='Email' {...register('email',{required:true})} />
                {errors.username?.type==='required' && <p className='text-danger m-2'>Email Required</p>}            
            
            </div>
            <div className='mb-3'>

                <input type="password" style={styles.input} className='form-control' placeholder='Password'{...register('password',{required:true})}/>
                {errors.username?.type==='required' && <p className='text-danger  m-2'>Password Required</p>}

            </div>
            <div className="">
            <button className='btn btn-success' type="submit">Register</button>
            </div>
        </form>
        {/* <p className='fs-6 px-3 text-center'>Already Registered!
            <Link to='/signin' className='px-1 link-success'>Login</Link>
            here
        </p> */}
        </div>
    </div>
    </div>
  )
}

export default Signup
