import React from 'react'
import { useLocation } from 'react-router-dom'
import { useForm } from 'react-hook-form'
import { useSelector } from 'react-redux'
import axios from 'axios'
import { useState } from 'react'

function Article() {

  let {handleSubmit,register}=useForm()
  let {currentUser,usertype}=useSelector(state=>state.userAuthorReducer)
  let [comment,setComment]=useState("")
  //get token from the local storage
  let token = localStorage.getItem('token')
  //create the axios with token
  let axiosWithToken=axios.create({
      headers:{Authorization:`Bearer ${token}`}
  })

  let {state}=useLocation()

  async function writeComment(obj){
    obj.username=currentUser.username
    console.log(state.articleId)
    //post the http request
    let res=await axiosWithToken.post(`http://localhost:4000/user-api/comment/${state.articleId}`,obj)
    if(res.data.message=="Comment is added"){
      setComment("Comment is Posted")
    }
  }
  return (
    
    <div className='card m-5 p-4'>
    <h1 className="">{state.title}</h1>
    <div className='d-flex gap-4 mt-3 mb-3'>
    <h5 className='fs-6'>Created On: <span>{state.dateOfCreation}</span></h5>
    <h5 className='fs-6'>Modified On: <span>{state.dateOfModification}</span></h5>
    </div>
    <div className='mt-3 mb-3'>
              {usertype == "Author" && (
                <>
                  <button
                    className="me-2 btn btn-warning "
                  >
                    Edit
                  </button>
                  <button
                    className="me-2 btn btn-warning "
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
    <p style={{ whiteSpace: "pre-line" }}>
      {state.Content}
    </p>
    <h3>{comment}</h3>
        {usertype == "User" && (
              <form onSubmit={handleSubmit(writeComment)}>
                <input
                  type="text"
                  {...register("comment")}
                  className="form-control mb-4 "
                  placeholder="Write comment here...."
                />
                <button type="submit" className="btn btn-success">
                  Add a Comment 
                </button>
              </form>
            )}
  </div>
           
  )
}

export default Article
