import React from 'react'
import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
function Followers() {

    let [Authors,setAuthors]=useState([])
    let navigate=useNavigate()
    let {currentUser}=useSelector(state=>state.userAuthorReducer)
   //get token from the local storage
   let token = localStorage.getItem('token')
   //create the axios with token
   let axiosWithToken=axios.create({
       headers:{Authorization:`Bearer ${token}`}
   })
 
     const getAllAuthors= async()=>{
         //get http request for all authors
         let res=await axiosWithToken.get('http://localhost:4000/author-api/authors')
         setAuthors(res.data.payload)
     }
     useEffect(()=>{
        getAllAuthors()
     },[])

  return (
    <div>
        {
        Authors.followers.map((obj,i)=><div key={i}>
            <div className='card d-flex gap-2'>
                <div className='card-body'>
                    <h2 className='m-2'>{obj.username}</h2>
                    <p>User details</p>
                </div>
            </div>
        </div>)
        }
    </div>
  )
}

export default Followers
