import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import axios from 'axios'
function FollowAuthors() {

    let {currentUser}=useSelector(state=>state.userAuthorReducer)
    let [Authors,setAuthors]=useState([])
    let [followStatus,setFollowStatus]=useState(false)

    //get token from the local storage
  let token = localStorage.getItem('token')
  //create the axios with token
  let axiosWithToken=axios.create({
      headers:{Authorization:`Bearer ${token}`}
  })

    const getAllAuthors= async()=>{
        //get http request for all authors
        let res=await axiosWithToken.get('http://localhost:4000/author-api/authors')
        setAuthors(res.data.payload)
    }

    useEffect(()=>{
        getAllAuthors()
    },[])

    const updateFollowStatus=()=>{
        setFollowStatus(true)
    }


  return (
    <div>
        
        Authors.length===0?(
            <h2 className='text-white text-center m-3'>No Authors yet...</h2>
        ):(
            {
                Authors.map(obj=><div key={obj._id}>
                    <div className='card m-2 p-3 bg-light rounded-2'>
                        <div className='card-body'>
                            <h3>{obj.username}</h3>
                            followStatus==false?
                            (
                                <button className='btn btn-info' onClick={updateFollowStatus}>Follow</button>
                            ):(
                                <button className='btn btn-secondary text-light'>Followed</button>
                            )
                            
                        </div>
                    </div>
                </div>
                )
            }
        )
        
    </div>
  )
}

export default FollowAuthors
