import React from 'react'
import { useNavigate } from 'react-router-dom';
import { useState,useEffect } from 'react';
import axios from 'axios';
function Articles() {


  const [articlesList, setArticlesList] = useState([]);
  let navigate=useNavigate()

  //get token from the local storage
  let token = localStorage.getItem('token')
  //create the axios with token
  let axiosWithToken=axios.create({
      headers:{Authorization:`Bearer ${token}`}
  })


  const getArticlesOfCurrentAuthor=async()=>{
    let res=await axiosWithToken.get(`http://localhost:4000/user-api/articles`)
    setArticlesList(res.data.payload)
  }


  const readArticleByArticleId=(articleObj)=>{
    navigate(`../article/${articleObj.articleId}`,{state:articleObj})
  }

    useEffect(()=>{
      getArticlesOfCurrentAuthor()
    },[])

  return (
      <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-4 mt-5">
            {articlesList.map((article) => (
              <div className="col" key={article.articleId}>
                <div className="card h-100">
                  <div className="card-body">
                    <h5 className="card-title">{article.title}</h5>
                    <p className="card-text">
                      {article.Content.substring(0, 80) + "...."}
                    </p>
                    <button className='btn btn-info px-4 m-auto d-block' onClick={()=>readArticleByArticleId(article)}>Read More</button>
                  </div>
                  <div className="card-footer">
                    <small className="text-body-secondary">
                      Last updated on {article.dateOfModification}
                    </small>
                  </div>
                </div>
              </div>
            ))}
      </div>
  )
}

export default Articles
