import React from 'react'
import { useLocation } from 'react-router-dom'
import { useForm } from 'react-hook-form'
function Article() {

  let {handleSubmit,register}=useForm()

  let {state}=useLocation()

  function writeComment(obj){
    console.log(obj)
  }
  return (
    
    <div  className='card m-5 p-4'>
    <h1 className="">{state.title}</h1>
    <div className='d-flex gap-4 mt-3 mb-3'>
    <h5 className='fs-6'>Created On: <span>{state.dateOfCreation}</span></h5>
    <h5 className='fs-6'>Modified On: <span>{state.dateOfModification}</span></h5>
    </div>
    <p style={{ whiteSpace: "pre-line" }}>
      {state.Content}
    </p>
    {currentUser.userType === "user" && (
              <form onSubmit={handleSubmit(writeComment)}>
                <input
                  type="text"
                  {...register("comment")}
                  className="form-control mb-4 "
                  placeholder="Write comment here...."
                />
                <button type="submit" className="btn btn-success">
                  Add a Comment <BiCommentAdd className="fs-3" />
                </button>
              </form>
            )}
  </div>
           
  )
}

export default Article
