import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import axios from 'axios'
function ArticlesByAuthor() {

    let {currentUser}=useSelector(state=>state.userAuthorReducer)
    let [articlesList,setArticles]=useState([])

    //get token from the local storage
    let token = localStorage.getItem('token')
    //create the axios with token
    let axiosWithToken=axios.create({
        headers:{Authorization:`Bearer ${token}`}
    })

    
        const getArticleOfCurrentAuthor = async()=>{
            let res=await axiosWithToken.get(`http://localhost:4000/author-api/articles/${currentUser.username}`)
            //console.log(res)
            setArticles(res.data.payload)
            //console.log(res.data.payload)
        }
    
        

        useEffect(()=>{
            getArticleOfCurrentAuthor()
          },[])

  return (
    <div className='w-25 d-block m-auto rows-cols-sm-1 rows-cols-md-2 rows-cols-lg-3 rows'>
        <h1 className='text-center text-white m-3'>{currentUser.username}'s articles</h1>
        {
        articlesList.map(articleObj=><div key={articleObj.id}>

            <div className='card bg-white m-3 rounded-3'>
                <div className='card-body'>
                    <h1>{articleObj.title}</h1>
                    
                    <button className='btn btn-info px-4 m-auto d-block' >Read More</button>
                </div>
            </div>

        </div>)
        }
    </div>
  )
}

export default ArticlesByAuthor
