import React from 'react'
import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
function Followers() {

    let [x,sx]=useState([])
    let [follower,setFollowerList]=useState([])
    let {currentUser}=useSelector(state=>state.userAuthorReducer)

   //get token from the local storage
   let token = localStorage.getItem('token')
   //create the axios with token
   let axiosWithToken=axios.create({
       headers:{Authorization:`Bearer ${token}`}
   })
 
     const getAllAuthors= async()=>{
         //get http request for all authors
         let res=await axiosWithToken.get('http://localhost:4000/author-api/authors')
         //console.log(res.data.payload)
         setAuthors(res.data.payload)
         console.log(x)
     }

     const getFollowersForAuthor = () => {
        const currentAuthor = x.find(y => y.username == currentUser.username);
        console.log(x)
        if (currentAuthor && currentAuthor.followers) {
            let followersList = currentAuthor.followers
                .filter(follower => follower.username) 
                .map(follower => follower.username); 
            setFollowerList(followersList);
        } else {
            setFollowerList([]);
        }
    };

     useEffect(()=>{
        getAllAuthors()
        getFollowersForAuthor()
     },[])


  return (
    <div>
        <h2 className='m-3 text-white'>{follower.length} Followers: </h2>
        {
        follower.map((obj,i)=><div className="m-2" key={i}>
            <div className='card d-flex gap-2 w-75 m-auto d-block'>
                <div className='card-body'>
                    <h3 className='mt-2 mb-2'>{obj.username}</h3>
                    <p>User details</p>
                </div>
            </div>
        </div>)
        }
    </div>
  )
}

export default Followers
