import React from 'react'
import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
function Followers() {

    let [Authors,setAuthors]=useState([])
    let [fol,setFol]=useState([])
    let {currentUser}=useSelector(state=>state.userAuthorReducer)

    //get token from the local storage
    let token = localStorage.getItem('token')
    //create the axios with token
    let axiosWithToken=axios.create({
        headers:{Authorization:`Bearer ${token}`}
    })
 
     const getAllAuthors= async()=>{
         //get http request for all authors
         let res=await axiosWithToken.get('http://localhost:4000/author-api/authors')
         setAuthors(res.data.payload)
     }
     
     const getFollowersForAuthor = () => {
        const currentAuthor = Authors.find(y => y.username === currentUser.username);
        if (currentAuthor && currentAuthor.followers) {
            let followersList = currentAuthor.followers
                .filter(follower => follower.username) 
                .map(follower => follower.username); 
                setFol(followersList);
        } else {
            setFol([]);
        }
    };

     useEffect(()=>{
        getAllAuthors()
        getFollowersForAuthor()
     },[])


  return (
    <div>
        <h2 className='m-3 ml-5 text-white'>{fol.length} Followers: </h2>
        {
        Authors.map((obj,ind)=><div className="m-2" key={ind}>
            <div className='card d-flex gap-2 w-50 m-auto d-block'>
                <div className='card-body'>
                    <h3 className='mt-2 mb-2'>{obj.username}</h3>
                    <p>User details</p>
                </div>
            </div>
        </div>)
        }
        <button onClick={getFollowersForAuthor()}>Click</button>
    </div>
  )
}

export default Followers
